import React, { useState, useEffect } from 'react'

//only need props as a param if we are passing in props to this component (we are going to here).
const Add = (props) => {
  let emptyPet = { name: '', age: '' }
  const [pet, setPet] = useState(emptyPet)


  const handleChange = (event) => {
    setPet({ ...pet, [event.target.name]: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    props.handleCreate(pet)
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name: </label>
        <input type="text" name="name" value={pet.name} onChange={handleChange} />
        <br />
        <br />
        <label htmlFor="age">Age: </label>
        <input type="number" name="age" value={pet.age} onChange={handleChange} />
        <input type='submit'/>
      </form>
    </>
  )
}

export default Add
