import React, { useState } from 'react'


const Edit = (props) => {
    const [pet, setPet] = useState({ ...props.pet })
    const [editAlert, setEditAlert] = useState(false)


    const handleChange = (event) => {
        setPet({ ...pet, [event.target.name]: event.target.value })

    }

    const dropdownFunction = () => {
        document.getElementById(`dropdown${props.pet.id}`).classList.toggle('show');
    }

    window.onclick = function (event) {
        if (!event.target.matches('.clickable-svg')) {
            var dropdowns = document.getElementsByClassName("dropdown-content");
            var i;
            for (i = 0; i < dropdowns.length; i++) {
                var openDropdown = dropdowns[i];
                if (openDropdown.classList.contains('show')) {
                    openDropdown.classList.remove('show');
                }
            }
        }
    }


    const openEditAlert = () => {
        setEditAlert(!editAlert)
    }
    const closeEditAlert = () => {
        setEditAlert(!editAlert)
    }
    const handleSubmit = (event) => {
        event.preventDefault()
        props.handleUpdate(pet)
        setEditAlert(false)
    }


    return (
        <div>
            <div className='dropdown-div'>
               <svg className='button-eyan'
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M16.3956 7.75734C16.7862 8.14786 16.7862 8.78103 16.3956 9.17155L13.4142 12.153L16.0896 14.8284C16.4802 15.2189 16.4802 15.8521 16.0896 16.2426C15.6991 16.6331 15.0659 16.6331 14.6754 16.2426L12 13.5672L9.32458 16.2426C8.93405 16.6331 8.30089 16.6331 7.91036 16.2426C7.51984 15.8521 7.51984 15.2189 7.91036 14.8284L10.5858 12.153L7.60436 9.17155C7.21383 8.78103 7.21383 8.14786 7.60436 7.75734C7.99488 7.36681 8.62805 7.36681 9.01857 7.75734L12 10.7388L14.9814 7.75734C15.372 7.36681 16.0051 7.36681 16.3956 7.75734Z"
                    fill="white"
                  />
                </svg>
                <div id={'dropdown' + props.pet.id} className='dropdown-content'>
                    <p onClick={openEditAlert}>Edit</p>
                    <p onClick={() => { props.handleDelete(props.pet) }}>Delete</p>
                </div>



                {editAlert ?
                    <div className='edit-overlay'>
                        <div className='edit-form-container'>
                            <div className='add-form-header'>
                                <h3>Edit Drink Post</h3>
                                <button onClick={closeEditAlert} className='close-add-button'>
                                    <svg
                                        width="24"
                                        height="24"
                                        viewBox="0 0 24 24"
                                        fill="none"
                                        xmlns="http://www.w3.org/2000/svg"
                                    >
                                    </svg>
                                </button>
                            </div>
                            <form className='add-form' onSubmit={handleSubmit}>
                                <label htmlFor="name">Name: </label>
                                <input
                                    type="text"
                                    name="name"
                                    value={pet.name}
                                    onChange={handleChange}


                                />
                                <br />
                                <br />
                                <label htmlFor="breed">Breed: </label>
                                <input
                                    type="text"
                                    name="breed"
                                    onChange={handleChange}
                                    value={pet.breed}
                                />
                                <br />
                                <br />
                                <label htmlFor="gender">gender: </label>
                                <input
                                    type="text"
                                    name="gender"
                                    onChange={handleChange}
                                    value={pet.gender}
                                />
                                <br />
                                <br />
                                <label htmlFor="color">Color: </label>
                                <input
                                    type="text"
                                    name="color"
                                    onChange={handleChange}
                                    value={pet.color}
                                />
                                <br />
                                <br />
                                <label htmlFor="size">Size: </label>
                                <input
                                    type="text"
                                    name="size"
                                    onChange={handleChange}
                                    value={pet.size}
                                />
                                <br />
                                <br />
                                <label htmlFor="age">Age: </label>
                                <input
                                    type="text"
                                    name="age"
                                    onChange={handleChange}
                                    value={pet.age}
                                />
                                <br />
                                <br />
                                <label htmlFor="description">Description: </label>
                                <input
                                    type="text"
                                    name="description"
                                    onChange={handleChange}
                                    value={pet.description}

                                />
                                <br />
                                <br />
                                <label htmlFor="image_url">image: </label>
                                <input
                                    type="text"
                                    name="image_url"
                                    onChange={handleChange}
                                    value={pet.image_url}
                                />
                                <br />
                                <input type="submit" />
                            </form>
                        </div>
                    </div>
                    :
                    null}
            </div>
        </div>
    )
}

export default Edit
